void setup_timer1()
{
	TCCR1A = 0;
	TCCR1B = 0;
	TCCR1C = 0;
	TCNT1 = 0;
	
	//TCCR1A |= (1<<COM1A1) | (1<<COM1A0) | (1<<WGM11);
	TCCR1A |= (1<<WGM11);
	//TCCR1B |= (1<<WGM13) | (1<<WGM12) | (1<<CS10);
	//prescaler = 8
	//TCCR1B |= (1<<WGM13) | (1<<WGM12) | (1<<CS11);
	
	//prescaler = 1024
	TCCR1B |= (1<<WGM13) | (1<<WGM12) | (1<<CS10) | (1<<CS12);
	
	//prescaler = 256
	//TCCR1B |= (1<<WGM13) | (1<<WGM12) | (1<<CS12);
	
	
	//OCR1A = 0xC000;	//set
	//OCR1A = 0xA000;
	//OCR1A = 100;
	//ICR1 = 0xF000;	//clear
	ICR1 = 100;	//clear
	OCR1A = 50;
	TIMSK1 =(1<<OCIE1A);
	//TIFR1 &= ~(1<<OCF1A);

}

void setup_timer2()
{
	TCCR2A = 0;
	TCCR2B = 0;
	TCNT2 = 0;
	OCR2A = 200;
	
	TCCR2A |= (1<<WGM21) | (1<<WGM20);
	// Prescaler = FCPU/1024
	TCCR2B |= (1<<CS20) | (1<<CS22) | (1<<WGM22);
	
	
	TIMSK2 = (1<<OCIE2A);
	
}

void setup_timer0()
{
	
	TCCR0A = 0;
	TCCR0B = 0;
	TCNT0 = 0;
	OCR0A = 200;
	
	TCCR0A |= (1<<WGM01) | (1<<WGM00);
	// Prescaler = FCPU
	TCCR0B |= (1<<CS00) | (1<<WGM02);
	//prescaler = x
	//TCCR0B |= (1<<CS00) | (1<<CS01) | (1<<WGM02);
	//prescaler FCPU/1024
	//TCCR0B |= (1<<CS00) | (1<<CS02) | (1<<WGM02);
	//prescaler FCPU/8
	//TCCR0B |= (1<<CS01) | (1<<WGM02);
	
	//TCCR0B |= (1<<FOC0B);
	
	
	//Enable Overflow Interrupt Enable
	//TIMSK0 |=(1<<TOIE0);
	TIMSK0 = (1<<OCIE0A);
	//TIMSK0 |=(1<<OCIE0B);
	
}

void setup_adc()
{
	//enable ADC, 1 1 1 = 128 prescaler = 8 MHz/128 = 62.5 kHz
	ADCSRA |= (1<<ADEN) | (1<<ADPS0) | (1<<ADPS1) | (1<<ADPS2) | (1<<ADIE) | (1<<ADSC);
	ADMUX |= (1<<ADLAR);
}

